mapper.insert:
/** 관리자 공지사항 작성 (insertNoticeWrite) **/
	INSERT INTO NOTICE(NTC_NO, NTC_TITLE, NTC_CONTENT, ADMIN_ID) VALUES(
	NTC_NO_SEQ.NEXTVAL
	, #{ntcTitle}
	, #{ntcContent}
	, #{adminId}
	)

mapper.update:
/** 관리자 공지사항 수정 (saveNoticeModify) **/
	UPDATE NOTICE
	SET
		NTC_TITLE = #{ntcTitle}
		, NTC_CONTENT = #{ntcContent}
		, NTC_REDATE = SYSDATE
	WHERE NTC_NO = #{ntcNo}

mapper.delete:
/** 관리자 공지사항 삭제 (deleteNotice) **/
	DELETE FROM NOTICE
	WHERE NTC_NO = #{ntcNo}
