mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY AQ_DATE DESC) AS n , AQ_NO , USER_ID , AQ_TITLE , AQ_CONTENT , AQ_DATE , AQ_REDATE , AQ_VIEWS , AQ_ANSWERS FROM QNA
WHERE AQ_TITLE LIKE '%'|| #{keyword} ||'%' WHERE USER_ID LIKE '%'|| #{keyword} ||'%' WHERE AQ_CONTENT LIKE '%'|| #{keyword} ||'%'
) WHERE N BETWEEN #{start} AND #{end}

mapper.insert:
insert into QNA_RPL values (
    QNA_RPL_SEQ.nextval
    , #{aaNo}
    , default
    , null
    , #{rplContent}
    , null
    , #{adminId}
)

mapper.update:
update QNA
set aq_answers = aq_answers + 1
where aq_no = #{aqNo}

update QNA
set aq_answers = aq_answers - 1
where aq_no = #{aqNo}

update QNA
set AQ_VIEWS = AQ_VIEWS + 1
where AQ_NO = #{aqNo}
