mapper.select:
/** 관리자의 사용자 관리 리스트 (selectUserList)**/ SELECT E.* FROM (SELECT D.* FROM (SELECT ROWNUM AS RO , USER_ID , USER_NAME , USER_EMAIL , ENABLE , USER_WR_DATE FROM (SELECT A.USER_ID , A.USER_NAME , A.USER_EMAIL , B.ENABLE , A.USER_WR_DATE FROM USER_TABLE A INNER JOIN MEMBER B ON A.USER_ID = B.ID WHERE 1=1
AND UPPER(A.USER_ID) LIKE UPPER('%'||#{searchUserId}||'%')
AND UPPER(A.USER_NAME) LIKE UPPER('%'||#{searchUserName}||'%')
AND B.ENABLE = '2'
ORDER BY A.USER_WR_DATE DESC ) C )D )E WHERE 1=1 AND RO BETWEEN #{start} AND #{end}

mapper.update:
/** 관리자의 사용자 관리 수정 (saveUserModify) **/
UPDATE USER_TABLE
SET
	  USER_PHONE = #{userPhone}
	, USER_UP_DATE = SYSDATE
WHERE USER_ID = #{userId}

mapper.select:
		/** 관리자의 사용자 신청자 요금제 개수 카운트 (selectMyPlanListCountAdmin)**/	
		SELECT COUNT(*)
		FROM USER_TABLE A
    	INNER JOIN PLAN_ORDER B ON A. USER_ID = B.USER_ID
		WHERE A.USER_ID=#{userId}

/** 사용자 리뷰를 단 요금제 개수 (selectOrderListCountAdmin)**/
	SELECT COUNT(*)
	FROM BIZ_REVIEW
	WHERE USER_ID=#{userId}
